import React, {useEffect, useState} from 'react';
import Button from './components/Button';
import ButtonContainer from './components/ButtonContainer';
import Summary from './components/Summary';

export default function App() {
  const [tasks, setTasks] = useState([]);
  const [isDone, setIsDone] = useState(0);
  const [isNotDone, setIsNotDone] = useState(0);

  useEffect (() => {
    const fetchTasks = async () => {
      const res = await fetch('http://localhost:3001/todos');

      const json = await res.json();
      setTasks(json);

    };

    fetchTasks();
  }, []);

  const teste = () => {
    let tempDone = 0;
    let tempNot = 0;
    tasks.filter(function() {
      if (tasks.done === true) {
        tempDone = tempDone + 1;
        setIsDone(tempDone);
      }
      if (tasks.done === false) {
        tempNot = tempNot + 1;
        setIsNotDone(tempNot);
      }

      return console.log(isDone, isNotDone);
    })
  }

  teste();
  return (
    <div>
      <h3>React Todos</h3>
      <ButtonContainer title="2019" />
      <ButtonContainer title="2020" />
      <ButtonContainer title="2021" />
      <Button />
      <Summary total={tasks.length} doneTrue={isDone} doneFalse={isNotDone}/>
    </div>
    );
}
