import React from 'react'
import { formatDateBR } from '../helpers/dateHelpers'

import css from '../css/styles.module.css';

export default function Todo({id, date, description, done}) {
  const modifyTodo = () => {
    const todoTask = document.getElementById({id});
    console.log(todoTask);
    todoTask.addEventListener('click', handleClickTodo(todoTask));
  }
  const handleClickTodo = (element) => {

    return event.done ? (event.className = 'red' && (done = false)) :
    (event.className = 'green accent-3' && (done = true))
  }
  return (
    <div 
      className={`card ${css.todo} ${done? 'green accent-3' : 'red'}`}
      onClick={modifyTodo}
      >
      <div>
        <strong>{formatDateBR(date)}</strong> - {description}
      </div>
    </div >
  );
}
