import React, { useState } from 'react'
import { formatDateBR } from '../helpers/dateHelpers'

import css from '../css/styles.module.css';
import axios from 'axios';

export default function Todo({id, date, description, done}) {
  const modifyTodo = () => {
    const [value, setValue] = useState(null);
    const todoTask = document.getElementById({id});
    setValue(todoTask.done);
    function defineValueReturn() {
      if (value === true) {
      const valueToFalse = todoTask.done = false;
      return valueToFalse;
      }
      if (value === false) {
        const valueToTrue = todoTask.done = true;
        return valueToTrue;
      }
    }
    console.log(todoTask);
    todoTask.addEventListener('click', handleClickTodo(value));
  }
  const handleClickTodo = async (value) => {
    await axios.put(`http://localhost:3001/todos/:${id}, {value}`)
  }
  return (
    <div 
      className={`card ${css.todo} ${done? 'green accent-3' : 'red'}`}
      onClick={modifyTodo}
      >
      <div>
        <strong>{formatDateBR(date)}</strong> - {description}
      </div>
    </div >
  );
}
