// eslint-disable-next-line
window.addEventListener('load', start);

function start() {
  const input = document.getElementById('textoPrincipal');

  input.addEventListener('keyup', converte);
}

function converte() {
  const input = document.getElementById('textoPrincipal');

  let consoantes = document.getElementById('consoantes');
  let variavel = document.getElementById('variavel');

  invertido(input.value);
  numerico(input.value.toUpperCase());
  csv(input.value);
  slug(input.value.toLowerCase());
  vogais(input.value);
  consoante(input.value);

}
function stringNormalize(str) {
  return str.normalize('NFD').replace(/[\u0300-\u036f]/g, '');
}


function invertido(textValue) {
  let invertido = document.getElementById('invertido');

  return invertido.value = textValue.split('').reverse().join('');
}

function numerico(textValue) {
  let numerico = document.getElementById('numerico');

  return numerico.value = stringNormalize(textValue)
    .replace(/['O']/g, '0')
    .replace(/['L']/g, '1')
    .replace(/['E']/g, '3')
    .replace(/['A']/g, '4')
    .replace(/['S']/g, '5')
    .replace(/['T']/g, '7');
}

function csv(textValue) {
  let csv = document.getElementById('csv');
  return csv.value = `"${textValue.split(' ').join('";"')}"`;
}

function slug(textValue) {
  let slug = document.getElementById('slug');
  return slug.value = textValue.split(' ').join('-');
}

function vogais(textValue) {
  let vogais = document.getElementById('vogais');
  return vogais.value = textValue.replace(/[aeiou]/gi, ' ');
}

function consoante(textValue) {
  let consoantes = document.getElementById('consoantes');
  return consoantes.value = textValue.replace(/[bcdfghjklmnpqrstvwxyz]/g, ' ');

}

function variavel(textValue) {

}

export default start;
