window.addEventListener('load', start);

function start() {
  const input = document.getElementById('textoPrincipal');

  input.addEventListener('keyup', converte);
}

function converte() {
  const input = document.getElementById('textoPrincipal');

  let invertido = document.getElementById('invertido');
  let numerico = document.getElementById('numerico');
  let csv = document.getElementById('csv');
  let slug = document.getElementById('slug');
  let vogais = document.getElementById('vogais');
  let consoantes = document.getElementById('consoantes');
  let variavel = document.getElementById('variavel');

  return (
    invertido.value = input.value.split('').reverse().join(''),

    numerico.value = input.value.split('').toUppercase().join(''),

    csv.value.split('').reverse().join(''),
    slug.value.split('').reverse().join(''),
    vogais.value.split('').reverse().join(''),
    consoantes.value.split('').reverse().join(''),
    variavel.value.split('').reverse().join('')
  )

}

export default start;
