import React from 'react';
import Input from './inputComponent';
import './index.css';
import './convert';

export default function Main() {
  return (
    <div className="container">
      <h2>react-text-transformer</h2>
      <Input id="mainText" label="Digite um texto aqui"/>
      <h3>Transformações</h3>
      <Input id="inverted" label="Texto invertido" copyText readOnly/>
      <Input id="numeric" label="Texto numérico" copyText readOnly/>
      <Input id="csv" label="Csv" copyText readOnly/>
      <Input id="slug" label="Slug" copyText readOnly/>
      <Input id="vowels" label="Somente vogais" copyText readOnly/>
      <Input id="consonants" label="Somente consoantes" copyText readOnly/>
      <Input id="variable" label="Variável" copyText readOnly/>
    </div>
  );
}
