import React from 'react';
import copy from './copyText';
import './index.css';

export default function Input(props) {
  const {
    id, 
    label, 
    readOnly = false, 
    copyText = false,
  } = props;
  return (
    <div className="col">
      <div className="input-field col s8">
        <label htmlFor={id}>{label && label + ':'}</label>
        <input type='text' id={id} readOnly={readOnly} className="input-field"/>
        {copyText && <copy inputId={id} />}
      </div>
    </div>
  )
}
