import React from 'react'
import { formatDateBR } from '../helpers/dateHelpers'

import css from '../css/styles.module.css';
import Check from './check';

export default function Todo({id, date, description, done, check = false}) {

  const handleButtonClick = () => {
    alert('OK!');
  };

  return (
    <div 
      className={`card ${css.todo} ${done? 'green accent-3' : 'red'}`}
      onClick={handleButtonClick}
    >
      <div>
        <strong>{formatDateBR(date)}</strong> - {description}
      </div>
      {check && <Check title="Feito" className={css.sameRow}/>}
    </div >
  );
}
